import { useState } from "react"

const Accordeon = ({ title, text }) => {

    const [showText, setShowText] = useState(true)

    const toggleShowText = () => {
        setShowText(!showText)
    }

    return (
        <div>
        <button>
        onClick={() => toggleShowText()}/>
        </button><p>{text}</p>

            {showText && <h1>{title}</h1>}
            {/* <hr />
            <div>
                <p>{text}</p>
            </div> */}



        </div>
    )
}

export default Accordeon
