import { useContext, useState } from "react"
import { Link } from "react-router-dom"
import BoxPrice from "../../components/BoxPrice/BoxPrice"
import { CartContext } from "../../context/CartContext"



const Cesta = () =>  {

    const { cart, setCart } = useContext(CartContext)
    const {choose, setChoose} = useContext(CartContext)
    const [carrito, setCarrito] = useState([])
    const [radio, setRadio] = useState('GRATIS')

    const addCoffee = (id) => {
        cart.map((coffee) => {
            if (coffee._id === id) {
                coffee.quantity --
            } 
            if (coffee.quantity === 0) {
                cart.splice(cart.indexOf(coffee), 1)
            }
        }) 
        setChoose(!choose)
    }

    const radioControl = (price) => {
        setRadio(price.target.value)

    }

    const lessCoffee = (id) => {
        cart.map((coffee) => {
            if (coffee._id === id) {
                coffee.quantity ++
            } 
        }) 
        setChoose(!choose)
    }

    const deleteCoffee = (id) => {
        cart.map((coffee) => {
            if (coffee._id === id) {
                cart.splice (cart.indexOf(coffee), 1)
            } 
        }) 
        setChoose(!choose)
    }

    useEffect(() => {
        const cartBox = cart.map((coffee) => {
            return (
                <div id='box-products' key={coffee._id}>
                    <div>
                        <img src={coffee.img} alt={coffee.name} />
                        <h4>{coffee.name}</h4>
                        <p>{coffee.price},00€</p>
                        <button onClick={() => addCoffee(coffee._id)}>-</button> <span>{coffee.quantity}</span><button onClick={() => lessCoffee(coffee._id)}>+</button>
                    </div>
                    <button onClick={() => deleteCoffee(coffee._id)}>X</button>
                </div>
            )
        }) 
        setCarrito(cartBox)
    },[choose])

    return (
        <div onChange={radioControl}>
            <p>GRATIS</p>
            <input type="radio" value='GRATIS' name="shipping" defaultChecked/>
            <p>de pago</p>
            <input type="radio" value='9,00€' name="shipping"/>

    <BoxPrice tipo={radio}/>
    <Link to={'/Form'}> HOLAAAAA</Link>
        </div>
    )
}

export default Cesta
