import { useContext, useEffect, useState } from "react"
import { CartContext } from "../../context/CartContext"




const BoxPrice = ({ tipo }) => {

    const { cart, setCart } = useContext(CartContext)
    const { shipping, setShipping } = useContext(CartContext)
    const { choose, setChoose } = useContext(CartContext)
    const [sendPrice, setSendPrice] = useState('GRATIS')
    const [subtotalPrice, setSubtotalPrice] = useState(0)
    const [IVAPrice,setIVAPrice] = useState(0)



    const totalShop = () => {
        let textSend = sendPrice
        let price = 0
        let send = 0

        cart.map(coffe => {
            price += (coffe.price * coffe.quantity)
            send += (coffe.price * coffe.quantity)
        })


        let iva = send - (send / 1.21)

        if (sendPrice !== 'GRATIS') {
            send += 9
            setShipping(send)
        } else {
            setShipping(price)
        }

        if (cart.length === 0) {

            return (
                <> </>
            )

        } else {
            return (
                <>
                    <h3>Total del carrito</h3>
                    <div>
                        <p>SUBTOTAL <span>{price.toFixed(2)} €</span></p>
                        <p>ENVIO <span>{sendPrice} </span></p>
                        <h5>TOTAL <span>{send.toFixed(2)} €</span></h5>
                        <p className="iva">Incluye {iva.toFixed(2)}€ de IVA</p>
                    </div>
                </>)
        }
    }








    return (
        <div>
            <p>Subtotal: {subtotalPrice.toFixed(2)} </p>
            <p>Envio : {sendPrice}</p>
            <p>IVA: {IVAPrice.toFixed(2)} €</p>
            <p>total: {shipping}</p>


        </div>
    )
}


export default BoxPrice
