import { useContext, useEffect, useState } from "react"
import { CartContext } from "../../context/CartContext"




const BoxPrice = ({ tipo }) => {

    const { cart, setCart } = useContext(CartContext)
    const { shipping, setShipping } = useContext(CartContext)
    const { choose, setChoose } = useContext(CartContext)
    const [ sendPrice, setSendPrice] = ('')

    const printBox = () => {

        let subtotal = 0
        let envio = 0
        let IVA = 0


        cart.map((coffee) => {
            subtotal += (coffee.price * coffee.quantity)
            envio += (coffee.price * coffee.quantity)
        })

        if (tipo !== 'GRATIS') {
            envio += 9
            setShipping(envio)
        } else {
            setShipping(subtotal)
        }

        if (envio === shipping) {
            sendPrice('9.00 €')
        } else {
            sendPrice()
        }



        IVA = envio - (envio / 1.21)

        return (
            <>
                <p>Subtotal: {subtotal.toFixed(2)} </p>
                <p>Envio : {sendPrice}</p>
                <p>IVA: {IVA.toFixed(2)} €</p>
                <p>{envio}</p>
            </>
        )

    }






    return (
        <div>


            {printBox()}

        </div>
    )
}


export default BoxPrice
