import { useContext, useEffect, useState } from "react"
import { Link } from "react-router-dom"
import BoxPrice from "../../components/BoxPrice/BoxPrice"
import { CartContext } from "../../context/CartContext"
import './Cesta.css'


const Cesta = () => {

    const { cart, setCart } = useContext(CartContext)
    const { choose, setChoose } = useContext(CartContext)
    const [carrito, setCarrito] = useState([])
    const [radio, setRadio] = useState('GRATIS')

    const addCoffee = (id) => {
        cart.map((coffee) => {
            if (coffee._id === id) {
                coffee.quantity--
            }
            if (coffee.quantity === 0) {
                cart.splice(cart.indexOf(coffee), 1)
            }
        })
        setChoose(!choose)
    }

    const radioControl = (price) => {
        setRadio(price.target.value)

    }

    const lessCoffee = (id) => {
        cart.map((coffee) => {
            if (coffee._id === id) {
                coffee.quantity++
            }
        })
        setChoose(!choose)
    }

    const deleteCoffee = (id) => {
        cart.map((coffee) => {
            if (coffee._id === id) {
                cart.splice(cart.indexOf(coffee), 1)
            }
        })
        setChoose(!choose)
    }

    useEffect(() => {
        const cartBox = cart.map((coffee) => {
            return (
                <div id="ca1" key={coffee._id}>
                    <div id="col">

                        <div id="chil1">
                            <button onClick={() => addCoffee(coffee._id)}>-</button> <p>{coffee.quantity}</p><button onClick={() => lessCoffee(coffee._id)}>+</button>
                        </div>

                        <div id="chil2">
                            <img src={coffee.img} alt={coffee.name} />
                        </div>

                        <div id="chil3">
                            <h5>{coffee.name}</h5>
                            <p>Paquete de café, 250 gr</p>
                        </div>

                    </div>
                    <div id="chil4">
                        <p>{coffee.quantity * coffee.price}</p>
                    </div>
                </div>
            )
        })
        setCarrito(cartBox)
    }, [choose])

    return (

        <div id="cesta-sect">
            <h3>Cesta</h3>

            <h4>Productos</h4>
            {carrito}

            <div id="radioBox" onChange={radioControl}>
                <h4>Seleccionar envío</h4>

                <div className="contRadio">
                    <div>
                        <input type="radio" value='GRATIS' name="shipping" defaultChecked />
                        <label>Envío 5-7 días</label>
                        <p>Opción estándar sin seguimiento</p>
                    </div>
                    <span>GRATIS</span>
                </div>

                <div>
                    <div>
                        <input type="radio" value='9,00€' name="shipping" />
                        <label>Envío urgente 24h</label>
                        <p>Recibe tu pedido en las siguientes 24h (Para pedidos realizados antes de las 13:00).</p>
                    </div>
                    <span>9,00 €</span>
                </div>

                <BoxPrice tipo={radio} />
                <Link to={'/Form'}> HOLAAAAA</Link>
            </div>
        </div>
    )
}

export default Cesta
