const Pasarela = () => {

    const [nueve, setNueve] = useState('')
    const {cart, setCart} = useContext(CartContext)
    const { shipping, setShipping } = useContext(CartContext)


    useEffect(() => {
        let price = 0
        cart.map((coffee) =>{
            price += (coffee.price * coffee.quantity)
        })
        if (price === shipping) {
            setNueve('GRATIS')
        }




    }, [])



    return (
        <div>


        <BoxPrice/>

        </div>
    )
}

