import { useContext, useEffect, useState } from "react"
import { CartContext } from "../../context/CartContext"



const ShopCart = () => {
    const { cart, setCart } = useContext(CartContext)
    const {choose, setChoose} = useContext(CartContext)
    const [carrito, setCarrito] = useState([])


    useEffect(() => {
        const cartBox = cart.map((coffee) => {
            return (
                <div id='box-products' key={coffee._id} style={{position:'fixed',
                right: '0',
                top: '50px',
                height: '100%'}}>
                    <div>
                        <img src={coffee.img} alt={coffee.name} />
                        <h4>{coffee.name}</h4>
                        <p>{coffee.price},00â‚¬</p>
                        <button>+</button> <span>{coffee.quantity}</span><button>-</button>
                    </div>
                    <button>X</button>
                </div>
            )
        }) 
        setCarrito(cartBox)
    },[choose])


    return (
        <div>

        {carrito}


            <button>Vaciar</button>
        </div>
    )
}

export default ShopCart
