import { useContext, useEffect, useState } from "react"
import { CartContext } from "../../context/CartContext"
import './ShopCart.css'


const ShopCart = () => {
    const { cart, setCart } = useContext(CartContext)
    const {choose, setChoose} = useContext(CartContext)
    const [carrito, setCarrito] = useState([])

    const addCoffee = (id) => {
        cart.map((coffee) => {
            if (coffee._id === id) {
                coffee.quantity --
            } 
            if (coffee.quantity === 0) {
                cart.splice(cart.indexOf(coffee), 1)
            }
        }) 
        setChoose(!choose)
    }

    const lessCoffee = (id) => {
        cart.map((coffee) => {
            if (coffee._id === id) {
                coffee.quantity ++
            } 
        }) 
        setChoose(!choose)
    }

    const deleteCoffee = (id) => {
        cart.map((coffee) => {
            if (coffee._id === id) {
                coffee.quantity ++
            } 
        }) 
        setChoose(!choose)
    }




    useEffect(() => {
        const cartBox = cart.map((coffee) => {
            return (
                <div id='box-products' key={coffee._id}>
                    <div>
                        <img src={coffee.img} alt={coffee.name} />
                        <h4>{coffee.name}</h4>
                        <p>{coffee.price},00â‚¬</p>
                        <button onClick={() => addCoffee(coffee._id)}>-</button> <span>{coffee.quantity}</span><button onClick={() => lessCoffee(coffee._id)}>+</button>
                    </div>
                    <button onClick={() => deleteCoffee(coffee._id)}>X</button>
                </div>
            )
        }) 
        setCarrito(cartBox)
    },[choose])


    return (
        <div  id="conteiner-cesta" style={{position:'fixed',
        right: '0',
        top: '50px',
        }}>

        {carrito}


            <button>Vaciar</button>
        </div>
    )
}

export default ShopCart
