import "./Bag.css"
import bagImage from '../assets/bag.svg'
import ShopCart from "../ShopCart/ShopCart"
import { useState } from "react"



const Bag = () => {

    const [showCart, setShowCart] = useState(true)

    const toggleShowCart = () => {
        setShowCart(!showCart)
    }


    return (
        <>
        <div id="bagLogo">
            <img src={bagImage} alt="Bag" className="bag" onClick={() => toggleShowText()}/>
            <span id="count"></span>

        </div>
        {showText && <p id="text-accordeon">{text}</p>}
        <ShopCart/>
        </>
    )
}

export default Bag
