import { useState } from "react"

const Accordeon = ({ title, text }) => {

    const [showPaula, setShowPaula] = useState(true)

    const toggleShowPaula = () => {
        setShowPaula(!showPaula)
    }

    return (
        <div>

            <h3>{title}</h3> <img src="./assets/flechahigh.png" alt="" />
            <hr />
            <div>
                <p>{text}</p>
            </div>



        </div>
    )
}

export default Accordeon
