import axios from "axios";
import { useContext, useEffect, useState } from "react";
import { CartContext } from "../../context/CartContext";
import './CafeApi.css'


const apiURL = 'https://cafe-de-altura-api.vercel.app/api/products'

const CafeApi = () => {

    const [coffees, setCoffees] = useState([])
    const [fetching, setFetching] = useState(true)
    const [items, setItems] = useState([]);
    const { cart, setCart } = useContext(CartContext)

    useEffect(() => {
        axios
            .get(apiURL)
            .then((response) => {

                const cafeSort = response.data.products.sort((a, b) => {
                    return (a.price - b.price)
                })

                const cafeMain = cafeSort.slice(0, 4)

                setCoffees(cafeMain)
                setFetching(false)
                setItems([])
            })
            .catch(err => console.error(err))
    }, [])

    const addItem = (coffee) => {
        const cafeAlto = {
            name: coffee.brand,
            price: coffee.price,
            _id: coffee._id,
            quantity: 1,
            img: coffee.img_url
        }

        const noDuplicate = cart.some(e => e._id === coffee.id)

        setCart(prev => [...prev, cafeAlto])

        cart.map((product) => {
            if (coffee._id ===product._id) {
                cart[cart.indexOf(product)].quantity++
            }else if (!coffee.available) {
                return
            } 

        })
        // if (noDuplicate) {

        //     cart.map((cof) => {
        //         if (noDuplicate) {
        //             console.log('HIGASO')
        //         }
        //         // if (cof._id === coffee._id) {
        //         //     return cof.quantity ++
        //         // } 

        //  } )
        // } else if  (!coffee.available) {
        //             return
        //         } else {
        //             setCart(prev => [...prev, cafeAlto])
        //         }

        // cart.map((cof) => {

        //     if (noDuplicate) {
        //         console.log('HIGASO')
        //     } else if (!coffee.available) {
        //         return
        //     } else {
        //         setCart(prev => [...prev, cafeAlto])
        //     }
        // })
    }

    return (
        <div id="news">

            {fetching && <h1>LOADING...</h1>}


            {coffees.map((coffee) => {
                return (
                    <div id='box-products' key={coffee._id}>
                        <img src={coffee.img_url} alt={coffee.brand} />
                        <h4>{coffee.brand}</h4>
                        <p>{coffee.price},00€</p>
                        <button onClick={() => addItem(coffee)}> Añadir</button>

                    </div>
                )
            })}



        </div>
    )
}

export default CafeApi
