import axios from "axios";
import { useEffect, useState } from "react";
import Button from "../Button/Button";
import './CafeApi.css'


const apiURL = 'https://cafe-de-altura-api.vercel.app/api/products'

const CafeApi = () => {

    const [coffees, setCoffees] = useState([])
    const[fetching, setFetching] = useState(true)

    useEffect(() => {
        axios
            .get(apiURL)
            .then((response) => {

                const cafeSort = response.data.products.sort((a, b) => {
                    return (a.price - b.price)
                })

                const cafeMain = cafeSort.slice(0,4)

                setCoffees(cafeMain)
                setFetching(false)
            })
            .catch(err => console.error(err))
    }, [])

    const addItem = productId => {
        const coffee = coffees.find(p => p._id === productId);
        setFetching([...fetch, coffee]);
      };

    return (
        <div id="news">

            {fetching && <h1>LOADING...</h1>}


            {coffees.map((coffee) => {
                return (
                    <div id= 'box-products' key={coffee._id}>
                        <img src={coffee.img_url} alt={coffee.brand} />
                        <h4>{coffee.brand}</h4>
                        <p>{coffee.price},00€</p>
                        <Button text={'Añadir'} onClick={() => addItem(coffee._id)}/>

                    </div>
                )
            })}



        </div>
    )
}

export default CafeApi
